#include <iostream>
using namespace std;

int gcd(int a, int b){
    while(b != 0){
        int temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

int main(){
    cout << "GCD of 48 and 18: " << gcd(48, 18) << endl;
    return 0;
}

